import React, { Component } from "react";

export default class FilterForm extends Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
    this.onPriceInputChange = this.onPriceInputChange.bind(this);
  }

  onPriceInputChange = (e) => {
    // to-do: implement handler
    this.setState({ value: e.target.value });
  };

  render() {
    return (
      <div>
        {/* Bind handlers and props */}
        <label htmlFor="name">Filter:</label>
        <input
          type="number"
          name="priceFrom"
          placeholder="Price from..."
          value={this.state.value}
          onChange={this.onPriceInputChange}
        />
        <input type="number" name="priceTo" placeholder="Price to..." />
      </div>
    );
  }
}
