import React, { Component } from "react";

export default class ToggleColumns extends Component {
  onCheckboxClick = (e, checked) => {
    console.log(e);
    console.log(checked);
    // to-do: implement checkbox click handler
  };

  render() {
    return (
      <div className="toggle-columns">
        {/* Bind handlers and props */}
        {Object.keys(this.props.columns).map((column, index) => {
          return (
            <div key={index}>
              <label>{column}</label>
              <input
                type="checkbox"
                onChange={(e) => this.onCheckboxClick(column, e.target.checked)}
              />
            </div>
          );
        })}
      </div>
    );
  }
}
