import React, { Component } from "react";
import "../styles/Search.css";
import ToggleColumns from "./ToggleColumns";
import ProductList from "./ProductList";
import FilterForm from "./FilterForm";

export default class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      priceFrom: "",
      priceTo: "",
      columns: {
        id: true,
        name: true,
        department: true,
        currency: true,
        price: true,
      },
    };
  }

  onPriceInputChange = (name, value) => {
    this.setState({ [name]: value });
  };

  filterProducts = (product) => {
    if (
      (this.state.priceFrom === undefined) &
      (this.state.priceTo === undefined)
    ) {
      return product.price;
    } else {
      return (
        (product.price > this.state.priceFrom) &
        (product.price < this.state.priceTo)
      );
    }
  };

  onCheckboxClick = (name, checked) => {
    this.setState({ columns: { ...this.state.columns, [name]: checked } });
  };

  render() {
    let displayedProducts = [this.props.products][0].filter(
      this.filterProducts
    );
    return (
      <div className="Products">
        <FilterForm
          priceFrom={this.state.priceFrom}
          priceTo={this.state.priceTo}
          onPriceInputChange={this.onPriceInputChange}
        />

        <ToggleColumns
          onCheckboxClick={this.onCheckboxClick}
          columns={this.state.columns}
        />

        <ProductList
          products={displayedProducts}
          columns={this.state.columns}
        />
      </div>
    );
  }
}
