import React, { useState } from "react";
import "../styles/Search.css";
import ToggleColumns from "./ToggleColumns";
import ProductList from "./ProductList";
import FilterForm from "./FilterForm";

const Search = ({ products }) => {
  const [status, setStatus] = useState({
    priceFrom: "",
    priceTo: "",
    columns: {
      id: true,
      name: true,
      department: true,
      currency: true,
      price: true,
    },
  });

  const onPriceInputChange = (name, value) => {
    setStatus({ ...status, [name]: value });
  };

  const filterProducts = (products) => {
    let filtedProducts = products.filter(
      (product) =>
        (status.priceFrom <= product.price) & (product.price <= status.priceTo)
    );
    if (status.priceTo && status.priceFrom <= status.priceTo) {
      return filtedProducts;
    } else {
      return products;
    }
  };

  const onCheckboxClick = (name, checked) => {
    setStatus({ ...status, [name]: checked });
  };

  let displayedProducts = filterProducts(products);

  return (
    <div className="Products">
      <FilterForm
        priceFrom={status.priceFrom}
        priceTo={status.priceTo}
        onPriceInputChange={onPriceInputChange}
      />

      <ToggleColumns
        onCheckboxClick={onCheckboxClick}
        columns={status.columns}
      />

      <ProductList products={displayedProducts} columns={status.columns} />
    </div>
  );
};

export default Search;
