import React, { Component } from "react";
import "../styles/Search.css";
import ToggleColumns from "./ToggleColumns";
import ProductList from "./ProductList";
import FilterForm from "./FilterForm";

export default class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      priceFrom: "",
      priceTo: "",
      columns: {
        id: true,
        name: true,
        department: true,
        currency: true,
        price: true,
      },
    };
  }

  onPriceInputChange = (name, value) => {
    this.setState({ [name]: value });
  };

  filterProducts = () => {
    // to-do: implement handler for filtering products by price range
  };

  onCheckboxClick = (name, checked) => {
    this.setState({ ...this.state.columns, [name]: checked });
    console.log(name, checked);
    console.log(this.state.columns);
  };

  render() {
    let displayedProducts = [this.props.products][0];
    return (
      <div className="Products">
        <FilterForm
          priceFrom={""}
          priceTo={""}
          onPriceInputChange={this.onPriceInputChange}
        />

        <ToggleColumns
          onCheckboxClick={this.onCheckboxClick}
          columns={this.state.columns}
        />

        <ProductList
          products={displayedProducts}
          columns={this.state.columns}
        />
      </div>
    );
  }
}
